import React, { Component } from 'react';
import './mainPage.css';
import Chart from '../../components/chart/chart';
import ForcastBox from '../../components/forcastBox/forcastBox';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSortDown, faSortUp } from '@fortawesome/free-solid-svg-icons'

class MainPage extends Component {
    state={
        moreBtnStatus: true,
    }

    moreBtnHandler = () => { 
        if(this.state.moreBtnStatus = true) {
            this.setState({moreBtnStatus : false})
        } else {
            this.setState({moreBtnStatus : true})
        } ;
    }

    render() { 
        return (
            <div className="MainPage" style={{backgroundImage: 'URL("https://images.unsplash.com/photo-1543772204-2cc21eb14509?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1287&q=80")'}}>
                {/* main box - current status  */}
                <div className="currentStatus">
                    <div className="cityName">New York, USA</div>
                    <div className="Temperature">7°</div>
                    <div className="status">Cloudy</div>
                    <div className="highLow">H: 11°  L: 6°</div>
                </div>

                {/* 7days forcast chart */}
                <div className="sevendaysTable">
                    <ForcastBox />
                    <ForcastBox />
                    <ForcastBox />
                    <ForcastBox />
                    <ForcastBox />
                    <ForcastBox />
                    <ForcastBox />
                </div>
                <div className="sevenDaysChart">
                    <Chart />
                </div>

                {/* Extra detail - wind speed / humidity / pressure / sunrise and sunset time */}
                <div className="moreBtn">
                    <button className="more" onClick={this.moreBtnHandler} style={{display : this.state.moreBtnStatus ? 'block' : 'none'}}>See More <br /><FontAwesomeIcon icon={faSortDown} /></button>
                </div>
                <div className="moreInfo">
                    <div className="container" style={{display : this.state.moreBtnStatus ? 'none' : 'block'}}>

                    </div>
                    <button className="more" onClick={this.moreBtnHandler} style={{display : this.state.moreBtnStatus ? 'none' : 'block'}}><FontAwesomeIcon icon={faSortUp} /><br /> See Less </button>
                </div>

            </div>
        );
    }
}

export default MainPage;
